provides.meta:
# Keep Carrot2 core interfaces and classes that implement them.
-keep class * extends org.carrot2.core.LocalComponent {
    !private <methods>;
    !private <fields>;
    !private <init>(*);
    !private <init>(*,*);
    !private <init>(*,*,*);
}

-keep class org.carrot2.core.** {
    public protected *;
}

# Keep local controller utility classes used from BeanShell.
-keep class org.carrot2.core.controller.* {
    public protected static <methods>;
    public protected static <fields>;
}
-keep class org.carrot2.core.controller.loaders.* {
    public protected static <methods>;
    public protected static <fields>;
}
